// src/components/OrderForm.js
import React, { useState, useEffect } from 'react';
import {
  Box, Container, Paper, Typography, TextField, Button,
  MenuItem, IconButton, Chip, Stack, Divider, Fab
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import Sidebar from './Sidebar';
import api from '../services/api';
import { useNavigate, useParams } from 'react-router-dom';
import { motion } from 'framer-motion';

const itemTypes = ['Shirt', 'Pants', 'Dress', 'Suit', 'Jacket', 'Curtain', 'Bed Sheet'];

export default function OrderForm() {
  const { id } = useParams();
  const isEdit = !!id;
  const navigate = useNavigate();

  const [customers, setCustomers] = useState([]);
  const [form, setForm] = useState({
    customer_id: '',
    items: [{ type: 'Shirt', qty: 1 }],
    total_cost: 0,
    status: 'Received',
    date_due: '',
    notes: ''
  });

  useEffect(() => {
    api.get('/customers').then(res => setCustomers(res.data));
    if (isEdit) {
      api.get(`/orders/${id}`).then(res => {
        const data = res.data;
        setForm({
          customer_id: data.customer_id,
          items: data.items,
          total_cost: data.total_cost,
          status: data.status,
          date_due: data.date_due || '',
          notes: data.notes || ''
        });
      });
    }
  }, [id, isEdit]);

  const addItem = () => {
    setForm({ ...form, items: [...form.items, { type: 'Shirt', qty: 1 }] });
  };

  const removeItem = (index) => {
    const newItems = form.items.filter((_, i) => i !== index);
    setForm({ ...form, items: newItems });
    calculateTotal(newItems);
  };

  const updateItem = (index, field, value) => {
    const newItems = [...form.items];
    newItems[index][field] = field === 'qty' ? parseInt(value) || 0 : value;
    setForm({ ...form, items: newItems });
    calculateTotal(newItems);
  };

  const calculateTotal = (items) => {
    const prices = { Shirt: 500, Pants: 600, Dress: 1200, Suit: 2500, Jacket: 1500, Curtain: 2000, 'Bed Sheet': 800 };
    const total = items.reduce((sum, i) => sum + (prices[i.type] || 0) * i.qty, 0);
    setForm(prev => ({ ...prev, total_cost: total }));
  };

  const handleSubmit = async () => {
    try {
      const payload = {
        ...form,
        quantity: form.items.reduce((s, i) => s + i.qty, 0)
      };
      if (isEdit) {
        await api.put(`/orders/${id}`, payload);
      } else {
        await api.post('/orders', payload);
      }
      navigate('/orders');
    } catch (e) {
      alert('Failed to save order');
    }
  };

  return (
    <Box sx={{ display: 'flex', bgcolor: '#f4f6f8', minHeight: '100vh' }}>
      <Sidebar />
      <Container sx={{ mt: 4, maxWidth: 800 }}>
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }}>
          <Paper elevation={3} sx={{ p: 4, borderRadius: 3 }}>
            <Typography variant="h5" sx={{ mb: 3, fontWeight: 700, color: '#1a3e72' }}>
              {isEdit ? 'Edit Order' : 'New Order'}
            </Typography>

            <Stack spacing={3}>
              <TextField
                select label="Customer" value={form.customer_id}
                onChange={e => setForm({ ...form, customer_id: e.target.value })}
                fullWidth required
              >
                {customers.map(c => (
                  <MenuItem key={c.id} value={c.id}>{c.name} ({c.phone})</MenuItem>
                ))}
              </TextField>

              <Divider>Items</Divider>

              {form.items.map((item, i) => (
                <Stack direction="row" spacing={2} key={i} alignItems="center">
                  <TextField
                    select label="Type" value={item.type}
                    onChange={e => updateItem(i, 'type', e.target.value)}
                    sx={{ width: 180 }}
                  >
                    {itemTypes.map(t => <MenuItem key={t} value={t}>{t}</MenuItem>)}
                  </TextField>
                  <TextField
                    label="Qty" type="number" value={item.qty} inputProps={{ min: 1 }}
                    onChange={e => updateItem(i, 'qty', e.target.value)}
                    sx={{ width: 100 }}
                  />
                  <IconButton color="error" onClick={() => removeItem(i)}>
                    <DeleteIcon />
                  </IconButton>
                </Stack>
              ))}

              <Button variant="outlined" startIcon={<AddIcon />} onClick={addItem}>
                Add Item
              </Button>

              <TextField
                label="Total Cost" value={form.total_cost} disabled
                InputProps={{ endAdornment: <Chip label="FCFA" size="small" /> }}
              />

              <TextField
                select label="Status" value={form.status}
                onChange={e => setForm({ ...form, status: e.target.value })}
              >
                {['Received', 'In Cleaning', 'Ready', 'Delivered'].map(s => (
                  <MenuItem key={s} value={s}>{s}</MenuItem>
                ))}
              </TextField>

              <TextField
                label="Due Date" type="date" value={form.date_due}
                onChange={e => setForm({ ...form, date_due: e.target.value })}
                InputLabelProps={{ shrink: true }}
              />

              <TextField
                label="Notes" multiline rows={2} value={form.notes}
                onChange={e => setForm({ ...form, notes: e.target.value })}
                placeholder="e.g. Urgent, delicate fabric..."
              />

              <Button
                variant="contained" size="large" onClick={handleSubmit}
                sx={{ mt: 2, py: 1.5, fontSize: '1.1rem' }}
              >
                {isEdit ? 'Update Order' : 'Create Order'}
              </Button>
            </Stack>
          </Paper>
        </motion.div>

        <Fab
          color="primary"
          sx={{ position: 'fixed', bottom: 32, right: 32 }}
          onClick={() => navigate('/orders')}
        >
          <AddIcon />
        </Fab>
      </Container>
    </Box>
  );
}
