import React, { useEffect, useState } from 'react';
import {
  Table, TableHead, TableBody, TableRow, TableCell,
  Container, Typography, Box, Button
} from '@mui/material';
import api from '../services/api';
import Sidebar from './Sidebar';
import { useNavigate } from 'react-router-dom';

export default function OrderList() {
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    api.get('/orders')
      .then(res => setOrders(res.data))
      .catch(() => alert('Failed to load orders'));
  }, []);

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Container sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom>Orders</Typography>
        <Button variant="contained" sx={{ mb: 2 }} onClick={() => navigate('/orders/new')}>
          New Order
        </Button>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Customer</TableCell>
              <TableCell>Items</TableCell>
              <TableCell>Total (FCFA)</TableCell>
              <TableCell>Status</TableCell>
              <TableCell>Received</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {orders.map(o => (
              <TableRow key={o.id}>
                <TableCell>{o.id}</TableCell>
                <TableCell>{o.customer_name}</TableCell>
                <TableCell>
                  {o.items.map(i => `${i.type} x${i.qty}`).join(', ')}
                </TableCell>
                <TableCell>{o.total_cost}</TableCell>
                <TableCell>{o.status}</TableCell>
                <TableCell>{o.date_received}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Container>
    </Box>
  );
}
