// src/components/Sidebar.js
import React from 'react';
import { Drawer, List, ListItem, ListItemText, Box, Divider, Typography } from '@mui/material';
import { Link, useLocation, useNavigate } from 'react-router-dom';

const drawerWidth = 240;

const links = [
  { to: '/', text: 'Dashboard' },
  { to: '/customers', text: 'Customers' },
  { to: '/orders', text: 'Orders' },
  { to: '/inventory', text: 'Inventory' },
  { to: '/payments', text: 'Payments' },
  { to: '/reports', text: 'Reports' },
];

export default function Sidebar() {
  const location = useLocation();
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': { width: drawerWidth, boxSizing: 'border-box' },
      }}
    >
      <Box sx={{ overflow: 'auto', mt: 8 }}>
        <Typography variant="h6" sx={{ px: 2, py: 1, fontWeight: 'bold' }}>
          Ice Pressing
        </Typography>
        <Divider />
        <List>
          {links.map((l) => (
            <ListItem
              button
              component={Link}
              to={l.to}
              key={l.to}
              selected={location.pathname === l.to}
            >
              <ListItemText primary={l.text} />
            </ListItem>
          ))}
        </List>
        <Divider />
        <List>
          <ListItem button onClick={handleLogout}>
            <ListItemText primary="Logout" />
          </ListItem>
        </List>
      </Box>
    </Drawer>
  );
}
