import React, { useEffect, useState } from 'react';
import { Table, TableHead, TableBody, TableRow, TableCell, Container, Typography, Box, Button, Chip } from '@mui/material';
import api from '../services/api';
import Sidebar from './Sidebar';
import { useNavigate } from 'react-router-dom';

export default function InventoryList() {
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    api.get('/inventory').then(res => setItems(res.data));
  }, []);

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Container sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom>Inventory</Typography>
        <Button variant="contained" sx={{ mb: 2 }} onClick={() => navigate('/inventory/new')}>
          Add Item
        </Button>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Reorder Level</TableCell>
              <TableCell>Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {items.map(i => (
              <TableRow key={i.id}>
                <TableCell>{i.name}</TableCell>
                <TableCell>{i.quantity}</TableCell>
                <TableCell>{i.reorder_level}</TableCell>
                <TableCell>
                  {i.quantity <= i.reorder_level ? 
                    <Chip label="Low Stock" color="error" size="small" /> : 
                    <Chip label="OK" color="success" size="small" />
                  }
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Container>
    </Box>
  );
}
