import React, { useState, useEffect } from 'react';
import { TextField, Button, Container, Box, Typography, MenuItem } from '@mui/material';
import api from '../services/api';
import Sidebar from './Sidebar';
import { useNavigate } from 'react-router-dom';

export default function OrderForm() {
  const [customers, setCustomers] = useState([]);
  const [form, setForm] = useState({
    customer_id: '',
    items: '[{"type":"Shirt","qty":1}]',
    total_cost: 0,
    status: 'Received',
    date_due: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    api.get('/customers').then(res => setCustomers(res.data));
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async () => {
    try {
      const payload = {
        ...form,
        items: JSON.parse(form.items),
        quantity: JSON.parse(form.items).reduce((s, i) => s + i.qty, 0)
      };
      await api.post('/orders', payload);
      navigate('/orders');
    } catch (e) {
      alert('Invalid JSON in items or server error');
    }
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Container sx={{ mt: 4 }}>
        <Typography variant="h5">New Order</Typography>
        <Box sx={{ mt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
          <TextField select name="customer_id" label="Customer" value={form.customer_id} onChange={handleChange} required>
            {customers.map(c => (
              <MenuItem key={c.id} value={c.id}>{c.name}</MenuItem>
            ))}
          </TextField>
          <TextField
            name="items"
            label='Items (JSON array)'
            multiline
            rows={3}
            value={form.items}
            onChange={handleChange}
            helperText='Example: [{"type":"Shirt","qty":2},{"type":"Dress","qty":1}]'
          />
          <TextField name="total_cost" label="Total Cost (FCFA)" type="number" value={form.total_cost} onChange={handleChange} required />
          <TextField name="status" label="Status" select value={form.status} onChange={handleChange}>
            {['Received','In Cleaning','Ready','Delivered'].map(s=><MenuItem key={s} value={s}>{s}</MenuItem>)}
          </TextField>
          <TextField name="date_due" label="Due Date" type="date" InputLabelProps={{ shrink: true }} value={form.date_due} onChange={handleChange} />
          <Button variant="contained" onClick={handleSubmit}>Create Order</Button>
        </Box>
      </Container>
    </Box>
  );
}
