// src/components/CustomerForm.js
import React, { useState, useEffect } from 'react';
import { TextField, Button, Container, Box, Typography, Paper } from '@mui/material';
import api from '../services/api';
import Sidebar from './Sidebar';
import { useNavigate, useParams } from 'react-router-dom';

export default function CustomerForm() {
  const { id } = useParams();
  const isEdit = !!id;
  const [form, setForm] = useState({ name: '', phone: '', address: '', email: '' });
  const navigate = useNavigate();

  useEffect(() => {
    if (isEdit) {
      api.get(`/customers/${id}`).then(res => setForm(res.data));
    }
  }, [id, isEdit]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async () => {
    try {
      if (isEdit) {
        await api.put(`/customers/${id}`, form);
      } else {
        await api.post('/customers', form);
      }
      navigate('/customers');
    } catch (e) {
      alert('Save failed');
    }
  };

  return (
    <Box sx={{ display: 'flex', bgcolor: '#f4f6f8', minHeight: '100vh' }}>
      <Sidebar />
      <Container sx={{ mt: 4, maxWidth: 600 }}>
        <Paper elevation={3} sx={{ p: 4, borderRadius: 3 }}>
          <Typography variant="h5" sx={{ mb: 3, fontWeight: 600, color: '#1a3e72' }}>
            {isEdit ? 'Edit Customer' : 'Add New Customer'}
          </Typography>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField name="name" label="Full Name" value={form.name} onChange={handleChange} required fullWidth />
            <TextField name="phone" label="Phone" value={form.phone} onChange={handleChange} required fullWidth />
            <TextField name="address" label="Address (YaoundÃ©)" value={form.address} onChange={handleChange} fullWidth />
            <TextField name="email" label="Email" type="email" value={form.email} onChange={handleChange} fullWidth />
            <Button variant="contained" size="large" onClick={handleSubmit}>
              {isEdit ? 'Update' : 'Create'} Customer
            </Button>
          </Box>
        </Paper>
      </Container>
    </Box>
  );
}
