import React, { useState } from 'react';
import { TextField, Button, Container, Box, Typography } from '@mui/material';
import api from '../services/api';
import { useNavigate, useParams } from 'react-router-dom';
import Sidebar from './Sidebar';

export default function CustomerForm() {
  const { id } = useParams();
  const isEdit = !!id;
  const [form, setForm] = useState({ name: '', phone: '', address: '', email: '' });
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async () => {
    try {
      if (isEdit) {
        await api.put(`/customers/${id}`, form);
      } else {
        await api.post('/customers', form);
      }
      navigate('/customers');
    } catch (e) {
      alert('Save failed');
    }
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Container sx={{ mt: 4 }}>
        <Typography variant="h5">{isEdit ? 'Edit' : 'Add'} Customer</Typography>
        <Box component="form" sx={{ mt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
          <TextField name="name" label="Name" value={form.name} onChange={handleChange} required />
          <TextField name="phone" label="Phone" value={form.phone} onChange={handleChange} required />
          <TextField name="address" label="Address" value={form.address} onChange={handleChange} />
          <TextField name="email" label="Email" value={form.email} onChange={handleChange} />
          <Button variant="contained" onClick={handleSubmit}>Save</Button>
        </Box>
      </Container>
    </Box>
  );
}
